//micro:bit BLE UUID
var ACCELEROMETERSERVICE_SERVICE_UUID       = 'e95d0753-251d-470a-a062-fa1922dfa9a8';
var ACCELEROMETERDATA_CHARACTERISTIC_UUID   = 'e95dca4b-251d-470a-a062-fa1922dfa9a8';
var accelerometer_device;
var accelerometer_characteristic;
var accX,accY,accZ;

define({
	// The input ports of the Javascript node, name of input and type
	inputs:{
	    // ExampleInput:'number',
	    // Available types are 'number', 'string', 'boolean', 'color' and 'signal',
	    connect:'signal',
	    disconnect:'signal',
	    mySignal:"signal"
	},

	// The output ports of the Javascript node, name of output and type
	outputs:{
	    // ExampleOutput:'string',
	    accX: "number",
	    accY: "number",
	    accZ: "number"
	},

	connect:function(inputs,outputs) {
		    navigator.bluetooth.requestDevice({
            filters: [{
              namePrefix: 'BBC micro:bit',
            }],
            optionalServices: [ACCELEROMETERSERVICE_SERVICE_UUID],

          })
          .then(device => {
            accelerometer_device = device;
            console.log("device", device);
            return device.gatt.connect();
          })
          //ACCELEROMETER
          .then(server =>{
            console.log("server", server)
            return server.getPrimaryService(ACCELEROMETERSERVICE_SERVICE_UUID);
          })
          .then(service => {
            console.log("service", service)
            return service.getCharacteristic(ACCELEROMETERDATA_CHARACTERISTIC_UUID)
          })
          .then(chara => {
            console.log("ACCELEROMETER:", chara)
            console.log("BLE接続が完了しました。");
            characteristic = chara;
            characteristic.startNotifications();
            characteristic.addEventListener('characteristicvaluechanged',onAccelerometerValueChanged);
          })  
          .catch(error => {
            console.log("BLE接続に失敗しました。もう一度試してみてください");
            console.log(error);
          });    
	},

	disconnect:function(inputs,outputs) {
		if (!accelerometer_device || !accelerometer_device.gatt.connected) return ;
        accelerometer_device.gatt.disconnect();
        console.log("BLE接続を切断しました。");
	},

	change:function(inputs,outputs) {
		this.runNextFrame();
		outputs.accX = accX;
	    this.flagOutputDirty("accX");
	    outputs.accY = accY;
	    this.flagOutputDirty("accY");
	    outputs.accZ = accZ;
	    this.flagOutputDirty("accZ");
	}
})

function onAccelerometerValueChanged(event) {
    AcceleratorX = event.target.value.getUint16(0)/1000.0;
    accX = AcceleratorX;
    console.log(AcceleratorX);

    AcceleratorY = event.target.value.getUint16(2)/1000.0;
    accY = AcceleratorY;

    AcceleratorZ = event.target.value.getUint16(4)/1000.0;
    accZ = AcceleratorZ;
}

define({
	inputs:{
      //Javascriptノードに、任意の入力ポートを指定できる。
      //書き方は、「変数名:'データ型'」
	    accX:'number'
	},
	outputs:{
      //Javascriptノードに、任意の出力ポートを指定できる
	    count:"boolean"
	},
  //データ型「signal」を入力ポートに指定した場合、その任意のシグナルをトリガーとして関数を実行することができる。
	mySignal:function(inputs,outputs) {
		this.previousValue = 0;
		this.previoustime = Date.now();
	},
	change:function(inputs,outputs) {
		var currenttime = Date.now();

		if(Math.abs(inputs.accX - this.previousValue) >10 && inputs.accX <20 && currenttime - this.previoustime >500){
		    this.sendSignalOnOutput("count");
		    this.previousValue = inputs.accX;
		    this.previoustime = currenttime;
		}
	}
})

define({
	// The input ports of the Javascript node, name of input and type
	inputs:{
	    // ExampleInput:'number',
	    // Available types are 'number', 'string', 'boolean', 'color' and 'signal',
	    start:'signal',
	    level1: 'signal',
	    level2: 'signal',
	    level3: 'signal',
	    apikey: 'string'
	},

	// The output ports of the Javascript node, name of output and type
	outputs:{
	    // ExampleOutput:'string',
	    lap: "signal"
	},

	// All signal inputs need their own function with the corresponding name that
	// will be run when a signal is received on the input.
	start:function(inputs,outputs) {
		// ...
		var _this = this;
        const ongaq = new Ongaq ({
            api_key: inputs.apikey,
            volume: 100,
            bpm: 120,
            onReady: ()=>{
                ongaq.start();

                my_drums.willMakeLap = ({ nextLap, meanTime })=>{
                    _this.sendSignalOnOutput("lap");
                }

                Noodl.eventEmitter.on("Tap", function(eventData) {
                    if(inputs.state == "play"){
                        ongaq.start();
                    }else{
                        ongaq.pause();
                    }
                });

                Noodl.eventEmitter.on("level1", function(eventData) {
                    ongaq.add( my_synth );

                });
                Noodl.eventEmitter.on("level2", function(eventData) {
                    ongaq.add( my_drums2 );

                });

                Noodl.eventEmitter.on("level3", function(eventData) {
                   ongaq.add( my_trumpet );
                });
            }
        })

        const my_drums = new Part ({
            sound: "small_cube_drums"
        })

        const my_synth = new Part ({
                        sound: "plain_keyboard",
                        measure: 1
                    })

        const my_drums2 = new Part ({
            sound: "my_band_drums"
            })

        const my_trumpet = new Part ({
            sound: "trumpet"
        })

        my_drums.add( 
            new Filter ({
            key: "kick",
            active: beat => beat%8 === 0
        }))

        my_drums.add(
        	new Filter ({
        		key: "hihat",
        		active: n => n % 16 === 4
        	})
        )

        my_drums.add(
        	new Filter ({
        		key: "tom2",
        		active: n => n % 16 === 4 ||n % 16 === 6 ||n % 16 === 8
        }))


        my_synth.add( 
            new Filter ({
        	key: [ "A2", "D2"],
        	length: 2,
        	active: (beat, measure) => beat % 16 === 0 && measure === 0 || beat % 16 === 6 && measure === 0 || beat % 16 === 12 && measure === 0
        }))

        my_drums2.add( 
            new Filter ({
            key: "snare",
            active: beat => beat % 8 === 4
        }))

        my_drums2.add( 
            new Filter ({
            key: "cymbal",
            active: beat => beat % 16 === 0
        }))

        my_trumpet.add( new Filter ({
        	key: [ "G3","G4","G5","G6"],
        	length: 16,
        	active: (beat, measure) => beat % 16 === 0 && measure === 0
        }))

        ongaq.add( my_drums );


	},

	tap:function(inputs,outputs) {
	    Noodl.eventEmitter.emit("Tap");
	},
	level1:function(inputs,outputs) {
	    Noodl.eventEmitter.emit("level1");
	},
	level2:function(inputs,outputs) {
	    Noodl.eventEmitter.emit("level2");
	},
	level3:function(inputs,outputs) {
	    Noodl.eventEmitter.emit("level3");
	},

	// This function will be called when any of the inputs have changed
	change:function(inputs,outputs) {
		// ...
	}
})
